# -*- coding: utf-8 -*-
"""
スクレイピング可否チェック＋実スクレイピングパイプライン
- 入力: keywords, basename (例: "tickets")
- 出力:
  ./csv/{basename}.csv
  ./csv/{basename}_filtered.csv
  ./robot_checked/{basename}_with_robot.csv
  ./document_checked/{basename}_with_tos.csv
  ./final_checked/{basename}_final.csv
  ./scraped/{basename}_scraped.csv
"""

import argparse

from get_url import collect_urls
from check_robot import append_robots_info
from check_document import append_tos_info
from scraping_permission import append_scraping_permission
from scrape import scrape_from_final
from filter_urls import filter_urls  # LLM/正規化 切替は filter_urls 内の use_llm で制御


def run_pipeline(
    keywords,
    basename="tickets",
    max_results=100,
    limit=None,
    # LLMトグル
    use_llm_filter=False,
    use_llm_scrape=False,
    # モデル指定
    llm_filter_model="qwen2.5:7b-instruct-q5_1",
    llm_scrape_model="qwen2.5:7b-instruct-q5_1",
    per_request_sleep=0.3,
    hop=True,
):
    # Step1: URL収集
    input_csv = f"./csv/{basename}.csv"
    print(f"🔍 Step1: URL収集 → {input_csv}")
    url_csv = collect_urls(keywords, input_csv, max_results=max_results)
    print(f"✅ URL収集完了: {url_csv}")

    # Step1.5: URLフィルタ
    filtered_csv = f"./csv/{basename}_filtered.csv"
    print(f"🪄 Step1.5: URLフィルタ → {filtered_csv} (LLM={'ON' if use_llm_filter else 'OFF'})")
    # use_llm_filter=True の時のみ LLM で“まとめ/おすすめ/ニュース/SNS/PR除外＋重複排除”
    # False の時は「正規化のみ」（http/https以外と完全重複URLを除去して通す）
    filter_urls(
        input_csv=url_csv,
        output_csv=filtered_csv,
        use_llm=use_llm_filter,
        model=llm_filter_model,
        batch_size=12,
        timeout=60,
        debug=False,
    )
    print(f"✅ URLフィルタ完了: {filtered_csv}")

    # Step2: robots.txt チェック
    robots_csv = f"./robot_checked/{basename}_with_robot.csv"
    print(f"🤖 Step2: robots.txt チェック → {robots_csv}")
    robots_out = append_robots_info(filtered_csv, robots_csv)
    print(f"✅ robots判定完了: {robots_out}")

    # Step3: 利用規約チェック
    tos_csv = f"./document_checked/{basename}_with_tos.csv"
    print(f"📑 Step3: 利用規約チェック → {tos_csv}")
    tos_out = append_tos_info(robots_out, tos_csv)
    print(f"✅ 利用規約判定完了: {tos_out}")

    # Step4: 可否判定まとめ（最新版に合わせて3引数）
    final_csv = f"./final_checked/{basename}_final.csv"
    print(f"⚖️ Step4: スクレイピング可否判定 → {final_csv}")
    final_out = append_scraping_permission(robots_out, tos_out, final_csv)
    print(f"✅ 可否判定完了: {final_out}")

    # Step5: 実スクレイピング
    scraped_csv = f"./scraped/{basename}_scraped.csv"
    print(f"🕷 Step5: 実スクレイピング → {scraped_csv} (LLM={'ON' if use_llm_scrape else 'OFF'})")
    scraped_out = scrape_from_final(
        final_out,
        scraped_csv,
        limit=limit,
        domain_sleep=per_request_sleep,
        hop=hop,
        llm=use_llm_scrape,
        llm_model=llm_scrape_model,
        llm_debug=False,
        strict=False,
    )
    print(f"✅ 実スクレイピング完了: {scraped_out}")

    return scraped_out


